document.addEventListener("DOMContentLoaded",()=>{

  // --- Quick links (conservado) ---
  const imgs = document.querySelectorAll(".quick-links__image");
  const items = document.querySelectorAll(".quick-links__item");
  function show(i){ if(!imgs.length) return; imgs.forEach(x=>x.classList.remove("active")); imgs[i%imgs.length].classList.add("active"); }
  items.forEach((el,i)=>{ el.addEventListener("mouseenter",()=>show(i)); el.addEventListener("focus",()=>show(i));
                          el.addEventListener("mouseleave",()=>show(0)); el.addEventListener("blur",()=>show(0)); });

  // --- Promo cards focus (conservado) ---
  const arrows=document.querySelectorAll(".promo-card a.arrow-link");
  arrows.forEach(a=>{
    a.addEventListener("focus",()=>{ a.parentElement&&a.parentElement.classList.add("is-focused"); });
    a.addEventListener("blur",()=>{ a.parentElement&&a.parentElement.classList.remove("is-focused"); });
  });

});



